import { View, Text,ActivityIndicator,StyleSheet  } from "react-native";

const Loader = () => {
  return (
    <View style={styles.loaderMain}>
      <Text>Loading</Text>
      <ActivityIndicator/>
    </View>
  );
};
const styles = StyleSheet.create({
  loaderMain: {
    flex: 1,
    backgroundColor: "#F0FFFF",
 
  },
});
export default Loader;
