import React, { useEffect, useState } from "react";
import { View, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import Home from "../home/Home.main";
import List from "../list/list.main";
import Loader from "../loader/Loader.main";
import Navbar from "../navbar/Navbar";
import Add from "../operation/Add.main";
import { RootState } from "../redux";
import { initialPages } from "./page.constants";
import { PageType } from "./page.type";

const PageController = () => {
  const [pages, setPages] = useState<PageType[]>(initialPages);
  const [loading, setLoading] = useState<boolean>(false);
  const { pageId } = useSelector((state: RootState) => state.PageReducer);
  // useEffect(() => {
  //   setTimeout(() => setLoading((prev) => !prev), 3000);
  // }, []);
  const GetSelectedPage = () => {
    switch (pageId) {
      case 1:
        return <Home />;
      case 3:
        return <Add />;
      case 2:
        return <List />;
    }
    const newPagesState: PageType[] = pages.map((page) =>
      page.id === pageId
        ? { ...page, selected: true }
        : { ...page, selected: false }
    );
    setPages(newPagesState);
  };

  return (
    <View style={{ flex: 1 }}>
      {loading ? (
        <Loader />
      ) : (
        <View style={styles.container}>
          {GetSelectedPage()}
          <Navbar />
        </View>
      )}
      
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#64262C",
    alignItems: "center",
    justifyContent: "center"
  },
});

export default PageController;
