import { useState } from "react";
import { View, Text,StyleSheet } from "react-native";
import Home from "../home/Home.main";
import List from "../list/List.main";
import Navbar from "../navbar/Navbar";
import Add from "../operation/Add.main";
import { initialPages } from "./page.constants";
import { PageType } from "./page.type";

const PageController = () => {
  const [pages, setPages] = useState<PageType[]>(initialPages);

  const GetSelectedPage = () => {
    const currentPageName: string = pages.find(
      (page) => page.selected
    ).pageName;
    switch (currentPageName) {
      case "Home":
        return <Home />;
      case "Add":
        return <Add />;
      case "List":
        return <List />;
    }
  };
  return (
    <View style={styles.container}>
      {GetSelectedPage()}
      <Navbar />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F0FFFF",
    alignItems: "center",
    justifyContent: "center",
  },
});

export default PageController;
