import { View, Text, StyleSheet, Image } from "react-native";
import { NewsType } from "./list.type";

const ListItem = (props) => {
  const currentItem:NewsType = props.news;
  return (
    <View style={styled.listItemContainer}>
      <View style={styled.listItemImage}>
        <Text>{currentItem.title}</Text>
        <Image
          style={styled.itemImage}
          source={require(props.imagePath)}
        />
      </View>
      <View style={styled.listItemText}>
        <Text>{currentItem.description}</Text>
      </View>
    </View>
  );
};

const styled = StyleSheet.create({
  listItemContainer: {
    borderColor: "#2F4F4F",
    borderWidth: 1,
    borderRadius: 10,
    height: 150,
    margin: 10,
    flexDirection: "row",
  },
  listItemImage: {
    borderColor: "red",
    borderWidth: 1,
    width: "35%",
    padding: 5,
  },
  itemImage: {
    width: 100,
    height: 100,
  },
  listItemText: {
    borderColor: "green",
    borderWidth: 1,
    width: "65%",
    padding: 5,
  },
});

export default ListItem;
