import { useEffect, useState } from "react";
import { View, Text } from "react-native";
import { useSelector } from "react-redux";
import Home from "../home/Home.main";
import List from "../list/List.main";
import Navbar from "../navbar/Navbar";
import Add from "../operation/Add.main";
import { RootState } from "../redux";
import { initialPages } from "./page.constants";
import { PageType } from "./page.type";

const PageController = () => {
  const [pages, setPages] = useState<PageType[]>(initialPages);
  const { pageId } = useSelector((state: RootState) => state.PageReducer);
  useEffect(() => {
    const newPagesState: PageType[] = pages.map((page) =>
      page.id === pageId
        ? { ...page, selected: true }
        : { ...page, selected: false }
    );
    setPages(newPagesState);
  }, [pageId]);
  const GetSelectedPage = () => {
    switch (pageId) {
      case 1:
        return <Home />;
      case 3:
        return <Add />;
      case 2:
        return <List />;
    }
  };
  return (
    <View>
      {GetSelectedPage()} <Navbar />
    </View>
  );
};

export default PageController;
